mapper.select:
SELECT page_id , title , satis_yn , meta_keyword , cpr_use_yn , cpr_cmrc_use_yn , cpr_chng_use_yn , charge_info , group_id , reg_userno , (SELECT user_nm FROM T_USER WHERE user_no = a.reg_userno) AS reg_usernm , GET_DATEFORMAT(reg_date) as reg_date , (SELECT IFNULL(ROUND(AVG(point),1),0) FROM T_SATISFY ts WHERE ts.contents_id = a.page_id) AS satis_score , TOT.TOTAL_CNT FROM T_INTROPAGE a JOIN ( SELECT COUNT(*) AS TOTAL_CNT FROM T_INTROPAGE WHERE 1 = 1
AND (UPPER(title) LIKE CONCAT('%', UPPER(#{searchtxt}), '%') OR INSTR(contents, UPPER(#{searchtxt})) > 0 OR INSTR(contents, LOWER(#{searchtxt})) > 0 )
AND satis_yn = #{satis_yn}
) TOT WHERE 1 = 1
AND (UPPER(title) LIKE CONCAT('%', UPPER(#{searchtxt}),'%') OR INSTR(contents, UPPER(#{searchtxt})) > 0 OR INSTR(contents, LOWER(#{searchtxt})) > 0 )
AND satis_yn = #{satis_yn}

SELECT @ROWNUM := @ROWNUM + 1 AS RNUM , page_id , title , satis_yn , meta_keyword , cpr_use_yn , cpr_cmrc_use_yn , cpr_chng_use_yn , charge_info , group_id , reg_userno , (SELECT user_nm FROM T_USER WHERE user_no = a.reg_userno) AS reg_usernm , GET_DATEFORMAT(reg_date) AS reg_date FROM T_INTROPAGE a , (SELECT @ROWNUM := 0) R WHERE 1 = 1
AND title = #{title}
AND contents = #{contents}

mapper.update:
INSERT INTO T_INTROPAGE (
	page_id
	, title
	, contents
	, satis_yn
	, meta_keyword
	, cpr_use_yn
	, cpr_cmrc_use_yn
	, cpr_chng_use_yn
	, charge_info
	, group_id
	, reg_userno
	, reg_date
	, upd_userno
	, upd_date
) VALUES ( 
	#{page_id}
	, #{title}
	, #{contents}
	, #{satis_yn}
	, #{meta_keyword}
	, #{cpr_use_yn}
	, #{cpr_cmrc_use_yn}
	, #{cpr_chng_use_yn}
	, #{charge_info}
	, #{group_id}
	, #{s_user_no}
	, DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s')
	, #{s_user_no}
	, DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s')
)

    UPDATE T_INTROPAGE
	SET
		title = #{title}
		, contents = #{contents}
		, satis_yn = #{satis_yn}
		, meta_keyword = #{meta_keyword}
		, cpr_use_yn = #{cpr_use_yn}
		, cpr_cmrc_use_yn = #{cpr_cmrc_use_yn}
		, cpr_chng_use_yn = #{cpr_chng_use_yn}
		, charge_info = #{charge_info}
		, group_id = #{group_id}
		, reg_userno = #{reg_userno}
		, reg_date = #{reg_date}
		, upd_userno = #{s_user_no}
		, upd_date = DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s')	
	WHERE  
		page_id = #{page_id}

    UPDATE T_INTROPAGE
	SET
		group_id = #{group_id}
	WHERE  
		page_id = #{page_id}
