mapper.select:
SELECT gubun , gubun_nm , reg_userno , (SELECT user_nm FROM T_USER WHERE user_no = a.reg_userno) AS reg_usernm , reg_date , use_yn , gubun_desc , (SELECT COUNT(code) FROM T_CODE WHERE gubun = a.gubun) AS code_cnt , TOT.TOTAL_CNT FROM T_CODEGUBUN a JOIN ( SELECT COUNT(*) AS TOTAL_CNT FROM T_CODEGUBUN WHERE 1 = 1
AND gubun_nm LIKE CONCAT('%', #{gubun_nm}, '%')
AND use_yn = #{use_yn}
) TOT WHERE 1=1
AND gubun_nm LIKE CONCAT('%', #{gubun_nm}, '%')
AND use_yn = #{use_yn}

		SELECT 
    		gubun
			, gubun_nm
			, use_yn
			, reg_userno
			, (SELECT user_nm FROM T_USER WHERE user_no = a.reg_userno) AS reg_usernm
			, reg_date
			, upd_userno
			, upd_date
		FROM T_CODEGUBUN a
		WHERE 
			1 = 1  			

			   AND gubun = #{gubun}


			   AND gubun_nm = #{gubun_nm}


			   AND reg_userno = #{reg_userno}


			   AND reg_date = #{reg_date}


			   AND upd_userno = #{upd_userno}


			   AND upd_date = #{upd_date}


			   AND use_yn = #{use_yn}

mapper.update:
INSERT INTO T_CODEGUBUN (
	gubun
	, gubun_nm
	, use_yn
	, gubun_desc
	, reg_userno
	, reg_date
	, upd_userno
	, upd_date
) VALUES ( 
	 #{gubun}
	, #{gubun_nm}
	, 'Y'
	, #{gubun_desc}
	, #{s_user_no}
	, DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s')
	, #{s_user_no}
	, DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s')
)

    UPDATE T_CODEGUBUN
	SET
		gubun_nm = #{gubun_nm}
		, use_yn = #{use_yn}
		, gubun_desc = #{gubun_desc}
		, upd_userno = #{s_user_no}
		, upd_date = DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s')
	WHERE  
		gubun = #{gubun}

mapper.select:
SELECT gubun , code , code_nm , use_yn , code_desc , sort , reg_userno , GET_DATEFORMAT(reg_date) as reg_date FROM T_CODE WHERE gubun = #{gubun}
AND code_nm LIKE CONCAT('%', #{code_nm}, '%')
ORDER BY sort

SELECT 
	count(*)
FROM T_CODE
WHERE 
	code = #{code}
	AND	gubun = #{gubun}

mapper.update:

        	SELECT IFNULL(MAX(sort),0)+1 FROM T_CODE WHERE gubun = #{gubun} 


		INSERT INTO T_CODE (
			gubun
			, code
			, code_nm
			, sort
			, code_desc
			, use_yn
			, reg_userno
			, reg_date
			, upd_userno
			, upd_date
		) VALUES ( 
			#{gubun}
			, #{code}
			, #{code_nm}
			, #{sort}
			, #{code_desc}
			, 'Y'
			, #{s_user_no}
			, DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s')
			, #{s_user_no}
			, DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s')
		)

    UPDATE T_CODE
	SET
		code_nm = #{code_nm}
		, code_desc = #{code_desc}
		, use_yn = #{use_yn}
		, upd_userno = #{s_user_no}
		, upd_date = DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s')		
	WHERE 
		code = #{code}
		AND	gubun = #{gubun}

mapper.delete:
 	DELETE FROM T_CODE 
	WHERE  
		code = #{code}
		AND	gubun = #{gubun}

mapper.update:
UPDATE T_CODE
SET 
	sort = #{sort}
WHERE 
	gubun = #{gubun} 
	AND code = #{code}


        	SELECT sort FROM T_CODE WHERE gubun = #{gubun} AND code = #{code}   


	    UPDATE T_CODE
		SET
			sort = sort - 1
		WHERE	
			sort > #{sort}
