mapper.select:
SELECT organ_no , organ_gb , (SELECT code_nm FROM T_CODE WHERE gubun = 'ORGAN_GB' AND code = a.organ_gb ) AS organ_gb_nm , domestic_yn , sort , organ_nm , organ_tel , organ_homepage , post , addr1 , addr2 , mt_organ_nm , image_file_id , (SELECT file_nm FROM T_COMMONFILE WHERE file_id = a.image_file_id) AS image_file_nm , use_yn , (SELECT user_nm FROM T_USER WHERE user_no = a.reg_userno) AS reg_usernm , reg_userno , GET_DATEFORMAT( reg_date ) AS reg_date , upd_userno , GET_DATEFORMAT( upd_date ) AS upd_date FROM T_ORGAN a WHERE 1 = 1
AND organ_gb = #{organ_gb}
AND domestic_yn = #{domestic_yn}
AND organ_nm LIKE CONCAT('%', #{organ_nm}, '%')
AND use_yn = #{use_yn}

mapper.update:

        	SELECT ifnull( MAX( sort ) , 0 ) + 1 FROM T_ORGAN  


		INSERT INTO T_ORGAN (
			organ_no
			, organ_gb
			, domestic_yn
			, sort
			, organ_nm
			, organ_tel
			, organ_homepage
			, post
			, addr1
			, addr2
			, mt_organ_nm
			, image_file_id
			, use_yn
			, reg_userno
			, reg_date
			, upd_userno
			, upd_date
		) VALUES ( 
			#{organ_no}
			, #{organ_gb}
			, #{domestic_yn}
			, #{sort}
			, #{organ_nm}
			, #{organ_tel}
			, #{organ_homepage}
			, #{post}
			, #{addr1}
			, #{addr2}
			, #{mt_organ_nm}
			, #{image_file_id}
			, #{use_yn}
			, #{s_user_no}
			, DATE_FORMAT( SYSDATE() , '%Y%m%d%H%i%s' )			
			, #{s_user_no}
			, DATE_FORMAT( SYSDATE() , '%Y%m%d%H%i%s' )			
		)

    UPDATE T_ORGAN

		organ_gb = #{organ_gb}
		, domestic_yn = #{domestic_yn}
		, organ_nm = #{organ_nm}
		, organ_tel = #{organ_tel}
		, organ_homepage = #{organ_homepage}
		, post = #{post}
		, addr1 = #{addr1}
		, addr2 = #{addr2}
		, mt_organ_nm = #{mt_organ_nm}
		, image_file_id = #{image_file_id}
		, use_yn = #{use_yn}
		, upd_userno = #{s_user_no}
		, upd_date = DATE_FORMAT( SYSDATE() , '%Y%m%d%H%i%s' )

	WHERE 
		organ_no = #{organ_no}

    UPDATE T_ORGAN
    SET 
    	sort = #{sort}
    WHERE 
		organ_no = #{organ_no}
